package baekjoon;import java.util.Scanner;public class p1562{    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int N = sc.nextInt();        long[][][] dp = new long[101][10][1<<10];        long sum = 0;        int visit = 1<<10;        for(int i=1;i<10;i++) dp[1][i][1<<i] = 1;        int bit = 0;        for(int i=2;i<=N;i++){            for(int j=0;j<=9;j++){                for(int k=0;k<visit;k++){                    bit = k | (1<<j);                    dp[i][j][bit] = (dp[i][j][bit]                        + (0<j ? dp[i-1][j-1][k] : 0)                        + (j<9 ? dp[i-1][j+1][k] : 0)%1000000000)                        %1000000000;                }            }        }        for(int i=0;i<10;i++) sum = (sum+dp[N][i][visit - 1]) % 1000000000;        System.out.println(sum);    }}